import { useState } from "react";

function Email() {
  const [email, setEmail] = useState('');

  const getEmail = (e) => {
    setEmail(e.target.value);
  }
    return(
        <div className="form-group">
            <label htmlFor="email">이메일</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={getEmail}
              required
            />
          </div>
    );
}

export default Email;
